import React from "react";

class TodoForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            textNote: '',
            activeNote: '',
            project: '',
            user: ''
        }
    }

    /* метод, который сохраняет в состояние введеные данные формы */

    handleChange(event) {
        this.setState(
            {
                [event.target.name]: event.target.value
            }
        )
    }

    handleChangeProject(event) {
        if (!event.target.selectedOptions) {
            this.setState(
                {
                    'project': []
                }
            )
            return
        }
        let project = []
        for (let i = 0; i < event.target.selectedOptions.length; i++) {
            project.push(event.target.selectedOptions.item(i).value)
        }
        this.setState(
            {
                'project': project
            }
        )
    }

    /* метод, который проверяет сохранились данные из формы в состояние или нет + отменяет
    * отправку формы */

    handleSubmit(event) {
        this.props.createTodo(this.state.textNote, this.state.activeNote, this.state.project, this.state.user)
        event.preventDefault()
    }

    render() {
        return (
            <form onSubmit={(event) => this.handleSubmit(event)}>

                <div className='form-group'>
                    <label htmlFor='text'>Text</label>
                    <input type='text' className='form-control' name='textNote' value={this.state.textNote}
                           onChange={(event) => this.handleChange(event)}/>
                </div>

                <div className='form-group'>
                    <label htmlFor='active'>Active</label>
                    <input type='text' className='form-control' name='activeNote' value={this.state.activeNote}
                           onChange={(event) => this.handleChange(event)}/>
                </div>

                <div className='form-group'>
                    <label htmlFor='project'>Project</label>
                    <select name='project' multiple onChange={(event) => this.handleChangeProject(event)}>
                        {this.props.project.map((item) => <option value={item.id}>{item.textNote}</option> )}
                    </select>
                </div>
                <input type='submit' className='btn btn-primary' value='Save' />
            </form>
        )
    }

}

export default TodoForm
